package main

import (
	"fmt"

	"math/big"

	"github.com/coinbase/kryptology/pkg/paillier"
)

func main() {

	var sec paillier.SecretKey
	var pub paillier.PublicKey

	//	pub, sec, _ := paillier.NewKeys()

	pub.N, _ = new(big.Int).SetString("", 10)
	pub.N2, _ = new(big.Int).SetString("", 10)
	sec.N = pub.N
	sec.N2 = pub.N2
	sec.Lambda, _ = new(big.Int).SetString("", 10)
	sec.Totient, _ = new(big.Int).SetString("", 10)
	sec.U, _ = new(big.Int).SetString("", 10)
	fmt.Printf("Pub N: %v\n", pub.N)
	fmt.Printf("Pub N2: %v\n", pub.N2)
	fmt.Printf("Sec: Lambda: %v\n", sec.Lambda)
	fmt.Printf("Sec: Totient: %v\n", sec.Totient)
	fmt.Printf("U: %v\n", sec.U)

	val1, _ := new(big.Int).SetString("123", 10)
	val2, _ := new(big.Int).SetString("456", 10)

	cipher1, _, _ := pub.Encrypt(val1)

	cipher2, _, _ := pub.Encrypt(val2)

	cipher3, _ := pub.Add(cipher1, cipher2)

	decrypted, _ := sec.Decrypt(cipher3)

	fmt.Printf("\na=%s, b=%s\n", val1, val2)

//	fmt.Printf("\nEnc(a)=%v, Enc(b)=%v\n", c1, c2)

	fmt.Printf("%s + %s = %s", val1, val2, decrypted)

}

